#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    char line[1000];
    if (fgets(line, sizeof(line), stdin)) {
        int digits[105];
        int digitsSize = 0;
        char *token = strtok(line, ",\n");
        while (token != NULL) {
            digits[digitsSize++] = atoi(token);
            token = strtok(NULL, ",\n");
        }

        int returnSize;
        int* result = plusOne(digits, digitsSize, &returnSize);

        printf("[");
        for (int i = 0; i < returnSize; i++) {
            printf("%d%s", result[i], (i < returnSize - 1) ? ", " : "");
        }
        printf("]");
        free(result);
    }
    return 0;
}

