#include <iostream>
#include <vector>
#include <sstream>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    string line;
    if (getline(cin, line)) {
        vector<int> digits;
        stringstream ss(line);
        string segment;
        while(getline(ss, segment, ',')) {
            digits.push_back(stoi(segment));
        }

        Solution sol;
        vector<int> result = sol.plusOne(digits);

        cout << "[";
        for(size_t i = 0; i < result.size(); ++i) {
            cout << result[i] << (i < result.size() - 1 ? ", " : "");
        }
        cout << "]";
    }
    return 0;
}

