use std::io::{self, BufRead};

fn main() {
    let stdin = io::stdin();
    let mut lines = stdin.lock().lines();
    if let Some(Ok(line)) = lines.next() {
        let mut nums: Vec<i32> = line.split(',')
            .map(|s| s.trim().parse().unwrap())
            .collect();
        print!("{}", Solution::remove_duplicates(&mut nums));
    }
