#include <stdio.h>
#include <stdlib.h>

int main() {
    char s[200005];
    if (fgets(s, sizeof(s), stdin)) {
        // fgets keeps newline. Manual remove if strict match needed, but problem ignores non-alphanumeric anyway so newline is ignored by correct logic.
        // But user implementation might rely on length. Let's removing trailing newline.
        size_t len = strlen(s);
        if (len > 0 && s[len-1] == '\n') s[len-1] = '\0';

        printf("%s", isPalindrome(s) ? "true" : "false");
    }
    return 0;
}

