use std::io::{self, BufRead};

fn main() {
    let stdin = io::stdin();
    let mut lines = stdin.lock().lines();
    if let Some(Ok(s)) = lines.next() {
        let s = s.trim().to_string();
        let result = Solution::is_valid(s);
        print!("{}", if result { "true" } else { "false" });
    }
