#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    char line[10000];
    if (fgets(line, sizeof(line), stdin)) {
        char *strs[205];
        int strsSize = 0;
        char *token = strtok(line, ",\n");
        while (token != NULL) {
            strs[strsSize] = malloc(strlen(token) + 1);
            strcpy(strs[strsSize], token);
            // trim optional newline
             size_t len = strlen(strs[strsSize]);
            if (len > 0 && strs[strsSize][len-1] == '\n') strs[strsSize][len-1] = '\0';
            strsSize++;
            token = strtok(NULL, ",\n");
        }
        char* res = longestCommonPrefix(strs, strsSize);
        printf("%s", res ? res : "");
    }
    return 0;
}

