#include <iostream>
#include <vector>
#include <sstream>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    string line;
    if (getline(cin, line)) {
        vector<string> strs;
        stringstream ss(line);
        string segment;
        while(getline(ss, segment, ',')) {
            // Remove potential trailing newline/cr
             if (!segment.empty() && segment.back() == '\r') segment.pop_back();
            strs.push_back(segment);
        }
        Solution sol;
        cout << sol.longestCommonPrefix(strs);
    }
    return 0;
}

